INSERT INTO public.components_bullet_point_bullet_points VALUES (170, 'Polycystic Ovary Syndrome (PCOS)', 'PCOS is a common hormonal disorder among women that is closely linked to Metabolic Syndrome. Women with PCOS have an increased risk of developing Metabolic Syndrome and its associated complications.');
