INSERT INTO public.components_bullet_point_bullet_points VALUES (193, 'Legumes and Pulses', 'Incorporate beans, lentils, chickpeas, and other legumes into meals for their high protein, fiber, and nutrient content.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (171, 'Hormonal Influence', 'Fluctuating hormone levels during menstrual cycles, pregnancy, and menopause can impact insulin sensitivity and lipid metabolism, contributing to the development of Metabolic Syndrome.');
