INSERT INTO public.components_bullet_point_bullet_points VALUES (48, 'Hypertension-Induced Kidney Damage', 'Prolonged high blood pressure can damage the delicate blood vessels in the kidneys, impairing their ability to effectively filter waste and regulate fluids.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (188, 'Reduced LDL Cholesterol', 'Plant-based diets have been shown to lower LDL cholesterol, often referred to as "bad" cholesterol. The high fiber content in plant foods helps to bind cholesterol and remove it from the body.');
