INSERT INTO public.components_bullet_point_bullet_points VALUES (197, 'Fiber', 'Nuts and seeds are excellent sources of dietary fiber. Soluble fiber found in these foods can help lower LDL cholesterol and improve overall heart health.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (227, 'Reduced Emotional Eating', 'Mindful eating fosters a non-judgmental attitude towards food and emotions, allowing individuals to respond to emotional cues without turning to food for comfort.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (560, ' Enhanced blood circulation', 'Physical activity increases blood flow, improving the delivery of oxygen and nutrients to body tissues. It also helps in reducing the levels of bad cholesterol (LDL) and increasing good cholesterol (HDL).');
