INSERT INTO public.components_bullet_point_bullet_points VALUES (190, 'Lower Triglycerides', 'Plant-based diets are typically low in saturated and trans fats, leading to lower levels of triglycerides, a type of fat that can contribute to heart disease.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (249, 'Homemade Meals', 'Prepare meals at home using fresh ingredients, allowing you to control the amount of added sugars in your dishes.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (143, 'Prevalence', 'The prevalence of Metabolic Syndrome in children and adolescents is rising globally, primarily due to sedentary lifestyles, poor dietary habits, and the obesity epidemic.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (169, 'Hormonal Factors', 'Women''s hormonal changes throughout their life stages, such as puberty, pregnancy, and menopause, can influence the development and progression of Metabolic Syndrome.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (186, 'Nutrient-Rich Foods', 'Plant-based diets are rich in dietary fiber, vitamins, minerals, and phytochemicals, which contribute to overall health and well-being.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (240, 'Reduce Sugary Beverages', 'Limit your intake of sugary drinks, including sodas, energy drinks, fruit juices, and sweetened coffee or tea. Choose water, herbal teas, or unsweetened alternatives.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (235, 'Weight Gain and Obesity', 'Excessive sugar intake contributes to weight gain and obesity due to its high calorie content and lack of nutritional value.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (237, 'Dental Health Problems', 'Consuming too much sugar can lead to tooth decay and cavities, as oral bacteria feed on sugar and produce acids that harm the teeth.');
