INSERT INTO public.components_bullet_point_bullet_points VALUES (245, 'Opt for Fresh Fruits', 'Satisfy your sweet tooth with whole fruits, which contain natural sugars and additional nutrients, fiber, and antioxidants.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (246, 'Select Whole Grains', 'Choose whole grain options like brown rice, whole wheat bread, and oats that provide sustained energy and are usually lower in added sugars compared to refined grains.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (263, 'Mindful Eating', 'Take time to savor and enjoy your meals, practicing mindful eating to enhance satisfaction and prevent overeating.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (219, 'Cooking at Home', 'Prepare homemade meals using fresh ingredients to have better control over the quality and nutritional value of your food.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (99, 'Fluid Alternatives', 'Limiting fluid intake may require substituting thirst-quenching beverages with ice chips, sugar-free gum, or hard candies.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (19, 'Protein Restriction', 'In the early stages of CKD, a moderate protein restriction may be recommended to reduce the production of waste products. The target protein intake is typically around 0.6-0.8 grams per kilogram of body weight per day.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (80, 'Reduced Kidney Function', 'A decline in kidney function impairs the body''s ability to regulate calcium, phosphorus, and PTH levels.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (75, 'Monitoring Phosphorus and Potassium', 'Individuals with kidney disease may need to monitor their phosphorus and potassium intake. Limit foods high in these minerals, such as dairy products, nuts, and certain fruits and vegetables.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (79, 'Parathyroid Hormone (PTH) Imbalance', 'ESRD can lead to elevated levels of PTH, which further contributes to bone loss and mineral imbalances.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (73, 'Managing Carbohydrate Intake', 'Choose complex carbohydrates with a low glycemic index to promote stable blood sugar levels. For example, include whole grains, legumes, fruits, and vegetables in your diet.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (61, 'Optimal Fluid Choices', 'Choose water as the primary source of hydration and limit intake of sugary beverages, caffeine, and alcohol, as they can impact blood pressure control.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (56, 'Sodium Recommendations', 'The American Heart Association recommends limiting sodium intake to no more than 2,300 milligrams (mg) per day for adults, with further reduction to 1,500 mg for those with hypertension or at risk.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (66, 'Medication Management', 'Phosphate binders may be prescribed to reduce phosphorus absorption from food and promote its excretion.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (69, 'Working with a Registered Dietitian', 'A registered dietitian who specializes in kidney health can provide personalized guidance on phosphorus management.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (68, 'Portion Control', 'Controlling portion sizes and monitoring food labels for phosphorus content are essential for managing phosphorus intake.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (78, 'Role of Calcium and Phosphorus', 'ESRD disrupts the balance of calcium and phosphorus, resulting in mineral imbalances that affect bone health.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (76, 'Staying Hydrated', 'Proper hydration is important for kidney health. Drink adequate water throughout the day, and limit sugary beverages and alcohol.');
