INSERT INTO public.components_bullet_point_bullet_points VALUES (63, 'Kidney''s Role', 'Healthy kidneys regulate phosphorus levels by excreting excess phosphorus through urine. In CKD, impaired kidney function can lead to phosphorus buildup.');
INSERT INTO public.components_bullet_point_bullet_points VALUES (59, 'Flavor with Herbs and Spices', 'Replace salt with herbs, spices, and other flavorful seasonings to enhance the taste of meals without relying on excessive sodium.');
